// Chatify.jsx

import React, { useState } from 'react';
import chatbotmsg from "../data/demoData";

const styles = {
  chatifyContainer: {
    width: '300px',
    border: '1px solid #ccc',
    padding: '10px',
    borderRadius: '5px',
  },
  messageContainer: {
    margin: '5px 0',
  },
  incomingMessage: {
    color: 'blue',
  },
  sentMessage: {
    color: 'green',
  },
  inputContainer: {
    marginTop: '10px',
    display: 'flex',
  },
  messageInput: {
    flex: '1',
    marginRight: '5px',
  },
};

const Chatify = () => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  const handleSendMessage = () => {
    if (newMessage.trim() !== '') {
      setMessages([...messages, { content: newMessage, type: 'sent' }]);
      setNewMessage('');
      // Uncomment this part to simulate a chatbot response after 3 seconds
      // setTimeout(() => {
      //   setMessages([...messages, { content: chatbotmsg[0], type: 'incoming' }]);
      // }, 3000);
    }
  };

  return (
    <div style={{ ...styles.chatifyContainer, ...styles.inputContainer }}>
      <div style={styles.chatifyContainer}>
        <div className="p-2 overflow-auto">
          {messages.map((message, index) => (
            <div
              key={index}
              style={{
                ...styles.messageContainer,
                ...{ textAlign: message.type === 'incoming' ? 'left' : 'right' },
                ...(message.type === 'incoming' ? styles.incomingMessage : styles.sentMessage),
              }}
            >
              {message.content}
            </div>
          ))}
        </div>

        <div style={styles.inputContainer}>
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Type your message..."
            style={styles.messageInput}
          />
          <button className="btn btn-primary" onClick={handleSendMessage}>
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default Chatify;
