import React, { useEffect, useState } from 'react';
import { FcAssistant } from "react-icons/fc";
import moment from 'moment';

import { IoPerson } from "react-icons/io5";
import { IoChatboxEllipsesOutline } from "react-icons/io5";

// import backgroundImage from '../images/chatBackground.jpg'
const url = "https://bot-server-nxtd.onrender.com/"

const Chatify = () => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  const handleSendMessage = () => {
    if (newMessage.trim() !== '') {
      setMessages([
        ...messages, 
        { content: newMessage, type: 'sent' }
      ]);
      setNewMessage('');
    }
  };

  const handleEnterKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSendMessage();
  };
  }

  return (
    <div className="d-flex p-2 justify-content-center align-items-center mt-5 mx-5 rounded-5">

      <div className="d-flex flex-column align-items-start border border-success border-2 rounded-4 p-2"style={{ width: '350px', height:'550px', overflow:'hidden'}}>
      <div className='d-flex bg-secondary justify-content-center align-items-center text-white w-100 rounded p-2' style={{height:'40px'}}>
      <div><IoChatboxEllipsesOutline /> Chatify </div> 
      </div>
        <div className="w-100 flex-fill" style={{overflowY: 'auto' }} >
          {messages.map((message, index) => (
            <div
              key={index}
              className={`d-flex justify-content-between  border border-success rounded-5 p-1 mx-2 mt-2 w-100 ${message.type === 'sent' ? 'justify-content-end' : 'justify-content-start'}`}
              style={{ color: message.type === 'incoming' ? 'blue' : 'green' }}
            >

              <div>{moment().format("LT")}</div>
              <div >
              {message.content }<IoPerson />
              </div>
            </div>
          ))}
        </div>
        <div className="d-flex  mt-2  border border-4 rounded-4 w-100">
          <input className="d-flex flex-grow-1 rounded-4 p-1"
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            onKeyDown={handleEnterKeyPress}
            placeholder="Type your message..."
          />
          <button className="btn btn-primary rounded-2 mx-1" id='send' onClick={handleSendMessage}>
            Send
          </button>
        </div>
      </div>
      </div>


  );
};

export default Chatify;
