import React, { useState } from 'react';
import chatbotmsg from "../data/demoData";

const Chatify = () => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  const handleSendMessage = () => {
    if (newMessage.trim() !== '') {
      setMessages([...messages, { content: newMessage, type: 'sent' }]);
      setNewMessage('');
      // setTimeout(()=>{
      //   setMessages([chatbotmsg[0],{content: chatbotmsg[0], type: 'incoming'}])
      // },3000);
    }
  };

  return (
    <div className="d-flex justify-content-center align-items-center mt-5 mx-5 border border-success">
      {/* <div style={{ width: '300px', border: '1px solid #ccc', padding: '10px', borderRadius: '5px' }}>
        <div className="p-2 overflow-auto " style={overflowY="auto"} >
          {messages.map((message, index) => (
            <div
              key={index}
              className={`message-container ${message.type === 'incoming' ? 'text-left' : 'text-right'}`}
              style={{ margin: '5px 0', color: message.type === 'incoming' ? 'blue' : 'green' }}
            >
              {message.content}
            </div>
          ))}
        </div> */}

        <div className="d-flex mt-4 mb-1">
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Type your message..."
            style={{ flex: '1', marginRight: '5px' }}
          />
          <button className="btn btn-primary" onClick={handleSendMessage}>
            Send
          </button>
        </div>
      </div>
    // </div>
  );
};

export default Chatify;
