import React, { useEffect, useState } from 'react';
// import botMessages from "../data/demoData";
import { FcAssistant } from "react-icons/fc";
import { IoPerson } from "react-icons/io5";
// import backgroundImage from '../images/chatBackground.jpg'
const url="https://bot-server-nxtd.onrender.com/"

const Chatify = () => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [id,setId]=useState(0);


  
  // const botMessageUpdate = ()={
    
    // }
    const handleSendMessage = () => {
      if (newMessage.trim() !== '') {
        setMessages([...messages, { content: newMessage, type: 'sent' }]);
        setNewMessage('');
        // setTimeout(()=>{
          //   setMessages([chatbotmsg[0],{content: chatbotmsg[0], type: 'incoming'}])
          // },3000);
        }
      };
      const handleKeyPress = (event) => {
        if (event.key === 'Enter') {
      // Call your function here
        handleEnterKeyPress();
      }
  };

  return (
    <div className="d-flex p-2 justify-content-center mt-5 mx-5 " >
      <div className="d-flex justify-content-center align-items-center border border-success border-2 rounded-4" style={{width:"350px", height:"600px"}}>
        <div className="p-2  " style={{ maxHeight: '200px', overflowY: 'auto' }} >
          {messages.map((message, index) => (
            <div
              key={index}
              className={`message-container ${message.type === 'incoming' ? 'text-left' : 'text-right'}`}
              style={{ margin: '5px 0', color: message.type === 'incoming' ? 'blue' : 'green' }}
            >
              {message.content}
            </div>
          ))}
        </div>
          <div className="d-flex  mt-4 mb-1 border border-4 rounded-4">
            <input className="d-flex flex-grow-1 rounded-4"
              type="text"
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              onKeyDown={handleEnterKeyPress}
              placeholder="Type your message..."
            />
            <button className="btn btn-primary rounded-2" id='send' onClick={handleSendMessage}>
              Send
            </button>
          </div>
        </div>
      </div>

  );
};

export default Chatify;
